function cleanText(text) {
    if (!text) return "";

    // Remove URLs
    text = text.replace(/http\S+|www\.\S+/g, "");

    // Remove emoji
    text = text.replace(/([\u2700-\u27BF]|[\uE000-\uF8FF]|[\u2011-\u26FF]|\uD83C[\uDC00-\uDFFF]|\uD83D[\uDC00-\uDFFF]|\uD83E[\uDC00-\uDFFF])/g, "");

    // Remove unwanted sections
    const patterns = [
        /Don't Miss:[\s\S]*?(?=\n\n|$)/gi,
        /Trending:[\s\S]*?(?=\n\n|$)/gi,
        /READ NEXT:[\s\S]*?(?=\n\n|$)/gi,
        /Read next:[\s\S]*?(?=\n\n|$)/gi,
        /Disclosure:[\s\S]*$/gi,
        /Copyright:[\s\S]*$/gi,
        /Photo by .*/gi
    ];

    for (const p of patterns) {
        text = text.replace(p, "");
    }

    // Remove bullets
    text = text.replace(/^[\-\‚Äì\‚Ä¢\*]\s*/gm, "");

    // Remove extra spaces
    text = text.replace(/ {2,}/g, " ");

    // Fix line breaks
    text = text.replace(/\n\s*\n\s*\n+/g, "\n\n");

    return text.trim();
}

